# Given a string containing just the characters '(' and ')',
# return the length of the longest valid (well-formed) parentheses substring.

def longest_parent(s: str) -> int:
    # working_sol (35.82%,  42.39%) time - O(n) | space O(n)
    if len(s) <= 1:
        return 0
    max_len = 0
    temp_indexes = [-1]
    opened = "("
    closed = ")"
    for x in range(len(s)):
        if s[x] == opened:
            temp_indexes.append(x)
        elif s[x] == closed:
            temp_indexes.pop()
            if len(temp_indexes) == 0:
                temp_indexes.append(x)
                continue
            temp_len = x - temp_indexes[-1]
            if max_len < temp_len:
                max_len = temp_len
    return max_len


# default index in temp_index == -1, correction for a whole correct parentheses string
# cuz if 0-20 correct it's going to lefter us with empty temp_indexes and size of (length(s) - 1)
# but with -1 we count max_len with | max_index - -1 == length(s) - which is correct

# Time complexity O(n): worst case we loop through every element of given string
# Space complexity O(n): worst case we create temp_indexes of the same length as given string | one big correct parent


def longest_parent2(s: str) -> int:
    # working_sol (50.6%, 89.94%) time O(n) | space O(1)
    opened = "("
    closed = ")"
    open_count = 0
    close_count = 0
    max_len = 0
    for x in range(len(s)):
        if s[x] == opened:
            open_count += 1
        if s[x] == closed:
            close_count += 1
        if close_count > open_count:
            close_count = 0
            open_count = 0
            continue
        if open_count == close_count:
            tempo_len = open_count + close_count
            if tempo_len > max_len:
                max_len = tempo_len
    open_count = 0
    close_count = 0
    for y in range(len(s) - 1, -1, -1):
        if s[y] == opened:
            open_count += 1
        if s[y] == closed:
            close_count += 1
        if open_count > close_count:
            close_count = 0
            open_count = 0
            continue
        if open_count == close_count:
            tempo_len = open_count + close_count
            if tempo_len > max_len:
                max_len = tempo_len
    return max_len

# Count every open or close signs and length of a correct parentheses is SUM when they're equal
# From left to right, checking IF closed is higher than open - which means we have all OPENED closed and extra CLOSED.
# From right to left, checking IF open is higher than closed - which means there's OPENED who can't be closed later.
# Time complexity O(n): we loop twice through a string n + n
# Space complexity O(1): we are not creating new arrays or changing input, but only 4 extra values: 1 + 1 + 1 + 1


