# Given a string containing just the characters '(' and ')',
# return the length of the longest valid (well-formed) parentheses substring.

def longest_parent(s: str) -> int:
    if len(s) <= 1:
        return 0
    start = 0
    end = 0
    max_len = 0
    parents = []
    opened = "("
    closed = ")"
    length = len(s)
    for x in range(length):
        if s[x] == opened:
            parents.append(opened)
            start = x
            for y in range(x + 1, length):
                if s[y] == opened:
                    parents.append(opened)
                elif s[y] == closed and len(parents) != 0:
                    parents.pop()
                    if len(parents) == 0:
                        end = y
                elif s[y] == closed and len(parents) == 0:
                    break
            parents = []
            if end == 0:
                max_len = 0
                continue
            tempo_len = end - start + 1
            if tempo_len > max_len:
                max_len = tempo_len
    return max_len


